import React, { useEffect, useState } from "react";
import {
  MapContainer,
  TileLayer,
  ZoomControl,
  LayersControl,
} from "react-leaflet";
import { PageContainer, Title } from "./styles/solution_style.js";

function Solution() {
  const API_KEY = "06aac0fd4ba239a20d824ef89602f311";
  // const API_KEY = "e6d8c9c20269bd6d193a610ab4f7d761";
  const [activeLayer, setActiveLayer] = useState("precipitation");

  const handleLayerChange = (event) => {
    setActiveLayer(event.name);
  };

  return (
    <PageContainer>
      <MapContainer
        center={[39.1, -76.1]}
        zoom={10}
        scrollWheelZoom={true}
        zoomControl={false}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a>'
        />
        <LayersControl position="topright">
          <LayersControl.BaseLayer
            name="Precipitation"
            checked={activeLayer === "precipitation"}
          >
            <TileLayer
              url={`https://tile.openweathermap.org/map/precipitation_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.BaseLayer>
          <LayersControl.BaseLayer
            name="Temperature"
            checked={activeLayer === "temperature"}
          >
            <TileLayer
              url={`https://tile.openweathermap.org/map/temp_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.BaseLayer>
          <LayersControl.BaseLayer
            name="Clouds"
            checked={activeLayer === "clouds"}
          >
            <TileLayer
              url={`https://tile.openweathermap.org/map/clouds_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.BaseLayer>
        </LayersControl>
        <ZoomControl position="topright" />
      </MapContainer>
    </PageContainer>
  );
}

export default Solution;
