import styled from "styled-components";
import { Link } from "react-router-dom";
import ship_topview from "../../imgs/ship_topview.jpg";
import landing_video from "../../imgs/landing_video.mov";

export const PageContainer = styled.div`
  height: calc(100vh - 60px);
  display: flex;
  width: 70%;
  flex-direction: column;
  justify-self: center;
  align-items: center;
  // &::after {
  //   content: "";
  //   // background: url(${ship_topview});
  //   // background-size: cover;
  //   background-position: center;
  //   overflow: hidden;
  //   opacity: 0.2;
  //   top: 0;
  //   left: 0;
  //   bottom: 0;
  //   right: 0;
  //   position: absolute;
  //   z-index: -1;
  // }
`;

export const Title = styled.h2`
  // position: relative;
  color: #fff;
  font-size: 5rem;
  // font-weight: 700;
  text-align: left;
  margin-bottom: 0px;
`;

export const Subtitle = styled.h3`
  color: #fff;
  // font-weight: bold;
  text-align: center;
  margin-top: 0px;
`;

export const Video = styled.video`
  position: fixed;
  left: 0;
  top: 0;
  min-width: 100%;
  min-height: 100%;
  width: auto;
  height: auto;
  z-index: -1;
  // filter: opacity(60%);
  filter: brightness(0.7) saturate(150%) grayscale(0.2);
`;

export const Demo = styled(Link)`
  padding: 10px 25px;
  border-radius: 25px;
  font-size: 1 rem;
  text-decoration: none;
  font-weight: 500;
  color: #fff;
  cursor: pointer;
  background: #4d4dff;

  &:hover {
    background: #fff;
    color: #00008b;
    transition: all 0.2s ease-in;
    text-decoration: none;
  }
`;
