import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import L from "leaflet";
import {
  MapContainer,
  TileLayer,
  ZoomControl,
  LayersControl,
  useMapEvents,
  Circle,
  LayerGroup,
  Marker,
  Popup,
} from "react-leaflet";
import { PageContainer, MapTileLayer } from "./styles/product_style.js";
import TemperatureLegend from "../components/legend_temp.js";

function Product() {
  // const API_KEY = "06aac0fd4ba239a20d824ef89602f311"; /* api key that works */
  const API_KEY = "e6d8c9c20269bd6d193a610ab4f7d761"; /* my api key */
  const center = [38.1, -76.1];
  const [activeLayer, setActiveLayer] = useState(null);

  function TemperatureLegendControl() {
    useMapEvents({
      overlayadd: (e) => {
        if (e.name === "Temperature") {
          console.log("Temperature overlay added");
          setActiveLayer("Temperature");
        }
      },
    });

    return null;
  }

  return (
    <PageContainer>
      <MapContainer
        center={center}
        zoom={8}
        scrollWheelZoom={true}
        zoomControl={false}
        // crossOrigin={true}
      >
        <MapTileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          // url="http://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}"
          // attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a>'
        />
        <LayersControl
          position="topleft"
          onChange={(event) => setActiveLayer(event.name)}
        >
          <LayersControl.Overlay name="Precipitation">
            <TileLayer
              url={`https://tile.openweathermap.org/map/precipitation/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Clouds">
            <TileLayer
              url={`https://tile.openweathermap.org/map/clouds_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Temperature">
            <TileLayer
              url={`https://tile.openweathermap.org/map/temp/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Wind">
            <TileLayer
              url={`https://tile.openweathermap.org/map/wind/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Pressure">
            <TileLayer
              url={`https://tile.openweathermap.org/map/pressure_cntr/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
        </LayersControl>
        <ZoomControl position="topright" />
        <TemperatureLegendControl />
        {console.log(activeLayer)}
        {activeLayer === "Temperature" && <TemperatureLegend />}
      </MapContainer>
    </PageContainer>
  );
}

export default Product;
