import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import L from "leaflet";
import {
  MapContainer,
  TileLayer,
  ZoomControl,
  LayersControl,
  useMapEvents,
  Circle,
  LayerGroup,
  Marker,
  Popup,
  Rectangle,
} from "react-leaflet";
import { PageContainer, MapTileLayer } from "./styles/product_style.js";
import TemperatureLegend from "../components/legend_temp.js";
import WindLegend from "../components/legend_wind.js";
import aisData from "./ais_messages.json";

function Product() {
  // const API_KEY = "06aac0fd4ba239a20d824ef89602f311"; /* api key that works */
  const API_KEY = "e6d8c9c20269bd6d193a610ab4f7d761"; /* my api key */
  const center = [25.1, -84.1];
  const [activeLayer, setActiveLayer] = useState(null);
  const rectangle = [
    [
      [31, -98],
      [18, -80],
    ],
  ];

  function LegendControl() {
    useMapEvents({
      overlayadd: (e) => {
        if (e.name === "Temperature") {
          setActiveLayer("Temperature");
        }
        if (e.name === "Wind") {
          setActiveLayer("Wind");
        }
      },
      overlayremove: (e) => {
        if (e.name === "Temperature") {
          setActiveLayer(null);
        }
        if (e.name === "Wind") {
          setActiveLayer(null);
        }
      },
    });

    return null;
  }

  return (
    <PageContainer>
      <MapContainer
        center={center}
        zoom={6}
        scrollWheelZoom={true}
        zoomControl={false}
        // crossOrigin={true}
      >
        <MapTileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          // url="http://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}"
          // attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a>'
        />
        {aisData.map((ais) => (
          <Circle center={[ais.latitude, ais.longitude]}></Circle>
        ))}
        <Rectangle bounds={rectangle} />
        <LayersControl position="topleft">
          <LayersControl.Overlay name="AIS">
            <TileLayer
              url={`https://tiles.marinetraffic.com/ais_helpers/shiptilesingle.aspx?output=png&sat=1&grouping=shiptype&tile_size=256&legends=1&zoom=
            {z}&X={x}&Y={y}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Precipitation">
            <TileLayer
              url={`https://tile.openweathermap.org/map/precipitation/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Clouds">
            <TileLayer
              url={`https://tile.openweathermap.org/map/clouds_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Temperature">
            <TileLayer
              url={`https://tile.openweathermap.org/map/temp_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Wind">
            <TileLayer
              url={`https://tile.openweathermap.org/map/wind_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Pressure">
            <TileLayer
              url={`https://tile.openweathermap.org/map/pressure_cntr/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
        </LayersControl>
        <ZoomControl position="topright" />
        <LegendControl />
        {activeLayer === "Temperature" && <TemperatureLegend />}
        {/* {activeLayer === "Wind" && <WindLegend />} */}
      </MapContainer>
    </PageContainer>
  );
}

export default Product;
