import { FaBars } from "react-icons/fa";
import { NavLink as Link } from "react-router-dom";
import styled from "styled-components";
import marview_logo from "../../imgs/marview_logo.png";

export const Nav = styled.nav`
  position: fixed;
  background: #ffffff;
  height: 80px;
  display: grid;
  top: 0;
  left: 0;
  width: 100%;
  grid-template-columns: 200px auto;
  z-index: 12;
`;

export const NavLink = styled(Link)`
  color: #00008b;
  text-decoration: none;
  padding: 1rem;
  cursor: pointer;
  &.active {
    color: #4d4dff;
  }
  &:hover {
    color: #4d4dff;
  }
`;

export const Bars = styled(FaBars)`
  display: none;
  color: #808080;
  @media screen and (max-width: 768px) {
    display: block;
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(-100%, 75%);
    font-size: 1.8rem;
    cursor: pointer;
  }
`;

export const NavMenu = styled.div`
  grid-column-start: 2;
  display: flex;
  justify-content: center;
  align-items: center;
  white-space: nowrap;
  @media screen and (max-width: 768px) {
    display: none;
  }
`;

export const Logo = styled(Link)`
  grid-column-start: 1;
  padding-left: 0.5rem;
  content: url(${marview_logo});
  width: 100%;
`;

export const MobileIcon = styled.div`
  display: none;
  @media screen and (max-width: 768px) {
    display: block;
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(-100%, 60%);
    font-size: 1.8rem;
    cursor: pointer;
  }
`;

export const MobileNav = styled.nav`
  background-color: #fff;
  position: fixed;
  top: 0;
  right: ${({ isOpen }) => (isOpen ? "0" : "-100%")};
  width: 100%;
  height: 100%;
  transition: 0.3s ease-in-out;
  z-index: 12;
`;

export const MobileNavItem = styled(Link)`
  display: flex;
  justify-content: center;
  align-items: center;
  text-decoration: none;
  height: 10vh;
  cursor: pointer;
  color: #00008b;
  font-size: 1.2rem;
  font-weight: bold;
  &:hover {
    color: #4d4dff;
  }
`;

export const NavItem = styled(Link)`
  color: #00008b;
  text-decoration: none;
  padding: 0 1rem;
  cursor: pointer;
  &.active {
    color: #4d4dff;
  }
  &:hover {
    color: #4d4dff;
  }
`;

export const NavBtn = styled.nav`
  display: flex;
  justify-content: center;
  align-items: center;
  @media screen and (max-width: 768px) {
    display: none;
  }
`;

export const NavBtnLink = styled(Link)`
  border-radius: 50px;
  background: #4d4dff;
  white-space: nowrap;
  padding: 10px 22px;
}
`;
