import styled from "styled-components";
import { Link } from "react-router-dom";
import ship_topview from "../../imgs/ship_topview.jpg";
import fuel_savings from "../../imgs/fuel_savings.svg";

export const PageContainer = styled.div`
  min-height: calc(100vh - 60px);
  padding-top: 60px;
  display: flex;
  flex-direction: column;
  @media screen and (max-width: 768px) {
    background: url(${ship_topview});
    background-postion: center;
  }
`;

export const HeadlineContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 60%;
  margin-left: 10%;
  margin-top: 1%;
`;

export const Title = styled.h1`
  color: #fff;
  font-size: 5rem;
  text-align: left;
  margin-bottom: 0;
  margin-top: 0;
  @media screen and (max-width: 768px) {
    font-size: 3rem;
  }
`;

export const Video = styled.video`
  width: 100%;
  height: 100%;
  position: absolute;
  object-fit: cover;
  z-index: -1;
  @media screen and (max-width: 768px) {
    display: none;
  }
`;

export const Demo = styled(Link)`
  padding: 10px 25px;
  border-radius: 25px;
  font-size: 1 rem;
  text-decoration: none;
  font-weight: 500;
  width: 150px;
  color: #fff;
  cursor: pointer;
  background: #4d4dff;
  margin-top: 15px;
  text-align: center;

  &:hover {
    background: #fff;
    color: #00008b;
    transition: all 0.2s ease-in;
    text-decoration: none;
  }
`;

export const Video2 = styled.video`
  width: 60%;
  @media screen and (max-width: 768px) {
    width: 100%;
  }
`;

export const ContentContainer1 = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 20px;
  background-color: #fff;
  @media screen and (max-width: 768px) {
    padding: 20px 0px 20px 0px;
  }
`;

export const ContentContainer2 = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 20px;
  background-color: #f8f8f8;
  @media screen and (max-width: 768px) {
    padding: 20px 0px 20px 0px;
  }
`;

export const ContentContainer3 = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  height: 300px;
  background-color: #f8f8f8;
  @media screen and (max-width: 768px) {
    padding: 20px 0px 20px 0px;
  }
`;

export const SubContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  margin-left: 10%;
  @media screen and (max-width: 768px) {
    justify-content: center;
  }
`;

export const SubContainer2 = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  @media screen and (max-width: 768px) {
    flex-direction: column;
  }
`;

export const TextBody = styled.div`
  display: flex;
  align-items: center;
  padding-right: 100px;
  @media screen and (max-width: 768px) {
    padding-right: 0px;
  }
`;

export const CardContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  gap: 16px;
  margin-top: 16px;
  @media screen and (max-width: 768px) {
    justify-content: center;
  }
`;

export const Subtitle = styled.h3`
  color: black;
  font-size: 2.5rem;
  @media screen and (max-width: 768px) {
    text-align: center;
  }
`;
