import React, { useEffect, useState } from "react";
import {
  MapContainer,
  TileLayer,
  ZoomControl,
  LayersControl,
} from "react-leaflet";
import {
  PageContainer,
  MapTileLayer,
  Legend,
} from "./styles/solution_style.js";

function TemperatureLegend({ stops }) {
  return (
    <div className="temperature-legend">
      <h3>Temperature</h3>
      <div
        className="color-scale"
        style={{
          backgroundImage: `linear-gradient(to right, ${stops
            .map((stop) => `${stop.color} ${stop.temp}%`)
            .join(", ")})`,
        }}
      >
        {stops.map((stop) => (
          <div
            key={stop.temp}
            className="stop-label"
            style={{ left: `${stop.temp}%` }}
          >
            <span className="label-text">{stop.temp}Â°C</span>
          </div>
        ))}
      </div>
    </div>
  );
}

const temperatureStops = [
  { temp: -65, color: "rgba(130, 22, 146, 1)" },
  { temp: -55, color: "rgba(130, 22, 146, 1)" },
  { temp: -45, color: "rgba(130, 22, 146, 1)" },
  { temp: -40, color: "rgba(130, 22, 146, 1)" },
  { temp: -30, color: "rgba(130, 87, 219, 1)" },
  { temp: -20, color: "rgba(32, 140, 236, 1)" },
  { temp: -10, color: "rgba(32, 196, 232, 1)" },
  { temp: 0, color: "rgba(35, 221, 221, 1)" },
  { temp: 10, color: "rgba(194, 255, 40, 1)" },
  { temp: 20, color: "rgba(255, 240, 40, 1)" },
  { temp: 25, color: "rgba(255, 194, 40, 1)" },
  { temp: 30, color: "rgba(252, 128, 20, 1)" },
];

function Solution() {
  const API_KEY = "06aac0fd4ba239a20d824ef89602f311";

  return (
    <PageContainer>
      <MapContainer
        center={[39.1, -76.1]}
        zoom={6}
        scrollWheelZoom={true}
        zoomControl={false}
      >
        <MapTileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a>'
        />
        <LayersControl position="topleft">
          <LayersControl.Overlay name="Precipitation">
            <TileLayer
              url={`https://tile.openweathermap.org/map/precipitation_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Clouds">
            <TileLayer
              url={`https://tile.openweathermap.org/map/clouds_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Temperature">
            <TileLayer
              url={`https://tile.openweathermap.org/map/temp_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Wind">
            <TileLayer
              url={`https://tile.openweathermap.org/map/wind_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Pressure">
            <TileLayer
              url={`https://tile.openweathermap.org/map/pressure_new/{z}/{x}/{y}.png?appid=${API_KEY}`}
            />
          </LayersControl.Overlay>
        </LayersControl>
        <ZoomControl position="topright" />
      </MapContainer>
      <Legend>
        <TemperatureLegend stops={temperatureStops} />
      </Legend>
    </PageContainer>
  );
}

export default Solution;
