import React, { useEffect } from "react";
import { MapContainer, TileLayer, ZoomControl } from "react-leaflet";
import { PageContainer, Title } from "./styles/solution_style.js";

const streets = tileLayer(
  "https://api.mapbox.com/styles/v1/{id}/tiles/{z}/{x}/{y}?access_token={accessToken}",
  {
    attribution:
      '<a href="https://www.openstreetmap.org/">OpenStreetMap</a>, <a href="https://creativecommons.org/licenses/by-sa/2.0/">CC-BY-SA</a>, <a href="https://www.mapbox.com/">Mapbox</a>',
    maxZoom: 18,
    id: "mapbox/streets-v11",
    tileSize: 512,
    zoomOffset: -1,
    accessToken:
      "pk.eyJ1IjoibWljaGFlbGNwIiwiYSI6ImNrN3V5eHVwNTAxMzgza2x1d2s1N3lkbmIifQ.Rmgw9tQHXVTL5_w6jvGjlw",
  }
);

function Solution() {
  const [map, setMap] = (useState < MapProp) | (null > null);

  useEffect(() => {
    if (!map) return;

    map.addLayer(streets);
  }, [map]);

  return (
    <PageContainer>
      <MapContainer
        center={[39.1, -76.1]}
        zoom={10}
        scrollWheelZoom={true}
        zoomControl={false}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a>'
        />
        <ZoomControl position="topright" />
      </MapContainer>
    </PageContainer>
  );
}

export default Solution;

